// Typography component types
export type MjoTypographyTag = "h1" | "h2" | "h3" | "h4" | "h5" | "p" | "span" | "none";

export type MjoTypographySize = "heading1" | "heading2" | "heading3" | "base" | "body1" | "body2" | "body3";

export type MjoTypographyWeight = "light" | "regular" | "medium" | "bold";

// ARIA level for heading elements (1-6)
export type MjoTypographyAriaLevel = 1 | 2 | 3 | 4 | 5 | 6;
